import { useEffect, useState } from "react";
import { collection ,addDoc, serverTimestamp, onSnapshot, query, where } from '@firebase/firestore';
import { auth, db } from '../firebase-config';

export const Chat = (props) =>{

        const {room} = props;

        const [messages, setMessages] = useState([]);
        const [newMessage, setNewMessage] = useState("");
        const messagesRef = collection(db, "messages");
        


        useEffect(() => {
            const queryMessages = query(messagesRef, where("room", "==", room));
            console.log(queryMessages);
            onSnapshot(queryMessages, (snapshot) => {
                let messages = [];
                snapshot.forEach((doc) => {
                    messages.push({...doc.data(), id: doc.id})
                });
                setMessages(messages)
            });
        }, []);


        const handleSubmit = async (e) => {
            e.preventDefault();
            if(newMessage === "") return;

            await addDoc(messagesRef, {
                text: newMessage,
                createAt: serverTimestamp(),
                user: auth.currentUser.displayName,
                room,
            });

            setNewMessage("");
        };

    return( 
    <div className="chat-app">
        <div>{messages.sort(message => message.createAt).map((messages) => <h3> {messages.text} </h3>)}</div>
        <form onSubmit={handleSubmit} className="new-message-from">
            <input 
            className="new-message-input"
             placeholder="Votre message..."
             onChange={(e) => setNewMessage(e.target.value)}
             value={newMessage}
             />
             

            <button type="submit" className="send-button">Envoyer</button>
        </form>
    </div>
    )
}
